mapper.update:
UPDATE `account` SET
balance_amount = balance_amount - #{amount},
freeze_amount = freeze_amount + #{amount}
WHERE id = #{accountId}

AND balance_amount >= #{amount}

UPDATE `account` SET
balance_amount = balance_amount + #{amount},
freeze_amount = freeze_amount - #{amount}
WHERE id = #{accountId}

AND freeze_amount >= #{amount}

UPDATE `account` SET
balance_amount = balance_amount - #{amount}
WHERE id = #{accountId}

AND balance_amount >= #{amount}
